jQuery(()=>jQuery('#carregar-mais').click(()=>{let a=jQuery('.artigo').length;jQuery.ajax({url:'/wp-admin/admin-ajax.php',type:'GET',data:{action:'carregar_mais_artigos',de:a},success:function(A){if(A.length==0){jQuery('#carregar-mais').before('<p id=\'sem-itens\'>Não há mais itens a serem carregados.</p>').prop('disabled',!0);return}let b='';for(const _ of A)b+=`
                        <li class="artigo">
                            <a href="${_.url}">
                                <img src="${_.imagem}" alt="${_.titulo}. ${_.creditoDaImagem}" title="${_.titulo}. ${_.creditoDaImagem}">
                            </a>
                            <div>
                                <a href="${_.url}">
                                    <h2>${_.titulo}</h2>
                                    <p>${_.resumo}</p>
                                    <span>${_.dataDePublicacao}</span>
                                </a>
                            </div>
                        </li>
                    `;jQuery('.artigos').append(b)}})}));
