jQuery(function(){jQuery('#carregar-mais').click(function(){let a=jQuery('.artigo').length;let b=jQuery(this).data('criterio');jQuery.ajax({url:'/wp-admin/admin-ajax.php',type:'GET',data:{action:'carregar_mais_busca',de:a,s:b},success:function(A){if(A.length==0){jQuery('#carregar-mais').before('<p id=\'sem-itens\'>Não há mais itens a serem carregados.</p>').prop('disabled',!0);return}let _='';for(const B of A)_+=`
                        <li class="artigo">
                            <a href="${B.url}">
                                <img src="${B.imagem}" alt="${B.titulo}. ${B.creditoDaImagem}" title="${B.titulo}. ${B.creditoDaImagem}">
                            </a>
                            <div>
                                <a href="${B.url}">
                                    <h2>${B.titulo}</h2>
                                    <p>${B.resumo}</p>
                                    <span>${B.dataDePublicacao}</span>
                                </a>
                            </div>
                        </li>
                    `;jQuery('.artigos').append(_)}})})});
